css:
@media(min-width: 767px) {
.header {
    top: 50px;
    height:110px;
}
 .top-bar {
    position: sticky;
    top: 0;
    z-index:900;
}}

.info-bar{
  background-color: #008990;
}


.header__row {
    display: flex !important;
    flex-direction: row !important;
  }

  .header__row .col-auto {
    order: 1 !important;
  }

  .header__row .col {
    order: 2 !important;
  }

  .header__row .d-none {
    order: 3 !important;
  }

  .header__row #search-bar {
    order: 4 !important;
  }

  .header__row .order-lg-last {
    order: 5 !important;
  }

.category-retail .product-card__name{
  display: flex !important;
  justify-content: center !important;
}

.category-retail .prices.product-card__prices{
  display: none !important;
}
