css:
@media(min-width: 767px) {
.header {
    top: 50px;
    height:110px;
}
 .top-bar {
    position: sticky;
    top: 0;
    z-index:900;
}}

.header__row {
    display: flex !important;
    flex-direction: row !important;
  }

  .header__row .col-auto {
    order: 1 !important;
  }

  .header__row .col {
    order: 2 !important;
  }

  .header__row .d-none {
    order: 3 !important;
  }

  .header__row #search-bar {
    order: 4 !important;
  }

  .header__row .order-lg-last {
    order: 5 !important;
  }

.prices__discount span {
  font-weight: bold !important;
  font-size: 110% !important;
}
